import { Dialog, DialogContent, DialogHeader } from "@/components/ui/dialog";
import { memo, useCallback, useState } from "react";
import MapStep from "./components/map-step";
import { ReviewStep } from "./components/review-step";
import StepIndicator from "./components/step-indicator";
import {
  CsvColumn,
  CsvFlowProps,
  FieldMappingItem,
  Meta,
  StepItems,
} from "./types";
import UploadStep from "./components/upload-step";

export type FlowSteps =
  | {
      step: StepItems.Upload;
    }
  | {
      step: StepItems.Map;
      data: Record<string, string>[];
      columns: CsvColumn[];
    }
  | {
      step: StepItems.Review;
      data: (Record<string, unknown> & Meta)[];
      fieldMappings: FieldMappingItem[];
    };

function CsvFlow(props: CsvFlowProps) {
  const {
    open,
    setOpen,
    fields,
    maxRows = 1000,
    maxFileSize = 1024 * 1024 * 2,
    enableCustomFields = false,
    customFieldReturnType = "object",
    showTemplateDownload = true,
    onImport,
  } = props;

  const [currentStep, setCurrentStep] = useState<FlowSteps>({
    step: StepItems.Upload,
  });

  const renderStep = useCallback(() => {
    switch (currentStep.step) {
      case StepItems.Upload:
        return (
          <UploadStep
            fields={fields}
            maxRows={maxRows}
            setStep={setCurrentStep}
            maxFileSize={maxFileSize}
            showTemplateDownload={showTemplateDownload}
          />
        );
      case StepItems.Map:
        return (
          <MapStep
            fields={fields}
            data={currentStep.data}
            columns={currentStep.columns}
            setStep={setCurrentStep}
            enableCustomFields={enableCustomFields}
          />
        );

      case StepItems.Review:
        return (
          <ReviewStep
            data={currentStep.data}
            fields={fields}
            fieldMappings={currentStep.fieldMappings}
            enableCustomFields={enableCustomFields}
            customFieldReturnType={customFieldReturnType}
            onImport={onImport}
            handleCloseDialog={() => {
              setOpen(false);
              setCurrentStep({
                step: StepItems.Upload,
              });
            }}
          />
        );
      default:
        return null;
    }
  }, [
    currentStep,
    fields,
    maxRows,
    maxFileSize,
    customFieldReturnType,
    enableCustomFields,
    showTemplateDownload,
    onImport,
    setOpen,
  ]);

  return (
    <Dialog
      modal
      open={open}
      onOpenChange={(isOpen) => {
        setOpen(isOpen);
        if (!isOpen) {
          setCurrentStep({ step: StepItems.Upload });
        }
      }}
    >
      <DialogContent
        className="h-[90vh] w-[90vw] max-w-[90vw] sm:max-w-[90vw] flex flex-col"
        onInteractOutside={(e) => {
          e.preventDefault();
        }}
      >
        <DialogHeader className="pb-6 border-b">
          <div className="flex space-x-4">
            <StepIndicator
              number={1}
              text="Upload a CSV file"
              description="Upload a CSV file to get started"
              active={currentStep.step === StepItems.Upload}
            />
            <StepIndicator
              number={2}
              text="Map fields"
              description="Map fields to the corresponding columns"
              active={currentStep.step === StepItems.Map}
            />
            <StepIndicator
              number={3}
              text="Review data"
              description="Review the data before importing"
              active={currentStep.step === StepItems.Review}
            />
          </div>
        </DialogHeader>

        <div className="grow overflow-hidden">{renderStep()}</div>
      </DialogContent>
    </Dialog>
  );
}

export default memo(CsvFlow);

export type CustomFieldReturnType = "object" | "json" | "flat";

/**
 * Props for the CSV Flow component.
 *
 * @interface CsvFlowProps
 *
 * @property {boolean} open - Indicates whether the CSV flow dialog is open.
 * @property {(v: boolean) => void} setOpen - Callback function to update the open state.
 * @property {FieldConfig[]} fields - An array of field configuration objects used to map CSV columns.
 *   Each object should include:
 *   - **columnName**: The internal name of the field (e.g., "Name", "Email") (Key that will be used when data is returned).
 *   - **displayName** (optional): A user-friendly name for the field. If omitted, `columnName` is used.
 *   - **columnRequired**: A boolean indicating whether the field is mandatory.
 *   - **type**: The expected data type for the field. One of "string", "number", "boolean", "email", or "date".
 *   - **example** (optional): An example value to include in the CSV template download.
 *   - **validations** (optional): An array of validations to apply. Validations can be of the following types:
 *     - **UniqueValidation**: `{ rule: "unique", allowEmpty?: boolean, errorMessage?: string, level?: "info" | "warning" | "error" }`
 *     - **RegexValidation**: `{ rule: "regex", value: string, flags?: string, errorMessage: string, level?: "info" | "warning" | "error" }`
 *     - **CustomValidation**: `{ rule: "custom", validate: (value: unknown, row: Record<string, unknown>) => boolean, errorMessage: string, level?: "info" | "warning" | "error" }`
 *
 * **Error Levels:** Default "error".
 *   - **"error"**: A critical validation error that must be resolved before import can proceed.
 *   - **"warning"** or **"info"**: These indicate less issues that are only informational and
 *     will not block the import.
 *
 * @property {number} [maxRows] - Optional. Maximum number of data rows to process. Defaults to 1000.
 * @property {number} [maxFileSize] - Optional. Maximum allowed file size (in bytes)
 * for the CSV file uploader. Defaults to 2MB.
 *
 * @property {boolean} [enableCustomFields] - When true, enables the custom fields functionality in the importer. Default is false.
 * @property {CustomFieldReturnType} [customFieldReturnType] Specifies how custom fields should be returned in the final data.
 * - "object": (default) Custom fields are returned as an object attached to the main data (e.g. under a `customFields` key).
 * - "json": Custom fields are returned as a JSON string.
 * - "flat": Custom fields are merged directly into the top-level of the final object.
 * @property {boolean} [showTemplateDownload] - When true, shows the download template button in the upload step. Default is true.
 * @property onImport - Callback function invoked when the import operation is triggered.
 *                      It receives an array of objects, where each object represents a row of imported CSV data.
 *
 * @example
 * import { useState } from "react";
 * import CsvFlow from "./features/csv-flow";
 * import { someFieldConfigs } from "./field-configurations";
 *
 * function App() {
 *   const [open, setOpen] = useState(false);
 *
 *   return (
 *     <>
 *       <button onClick={() => setOpen(true)}>Open CSV Flow</button>
 *       <CsvFlow
 *         open={open}
 *         setOpen={setOpen}
 *         fields={someFieldConfigs}
 *         maxRows={500}         // Optional: override default max rows (default is 1000)
 *         maxFileSize={1024 * 1024} // Optional: override default max file size (default is 2MB)
 *         showTemplateDownload={true} // Optional: show download template button (default is true)
 *       />
 *     </>
 *   );
 * }
 */
export type CsvFlowProps = {
  open: boolean;
  setOpen: (v: boolean) => void;
  fields: FieldConfig[];
  maxRows?: number;
  maxFileSize?: number;
  enableCustomFields?: boolean;
  customFieldReturnType?: CustomFieldReturnType;
  showTemplateDownload?: boolean;
  onImport: (data: Record<string, unknown>[]) => void;
};

export enum StepItems {
  Upload = "Upload",
  Map = "Map",
  Review = "Review",
}

type FieldTypes = "string" | "number" | "boolean" | "email" | "date";

/**
 * Configuration for a single field in the CSV.
 *
 * @interface FieldConfig
 * @property {string} columnName - The internal field name used for mapping (e.g., "Name", "Email").
 * @property {string} [displayName] - An optional human-friendly name for display purposes. If not provided, `columnName` is used.
 * @property {boolean} columnRequired - Indicates whether the field is mandatory.
 * @property {"string" | "number" | "email" | "date"} type - The expected data type for the field.
 * @property {Validation[]} [validations] - An array of additional validations to apply to this field.
 * @property {unknown} [example] - An optional example value to include in the CSV template.
 *
 * @example
 * const fieldConfig: FieldConfig = {
 *   columnName: "email",
 *   displayName: "Email Address",
 *   required: true,
 *   type: "email",
 *   example: "john.doe@example.com",
 *   validations: [
 *     {
 *       rule: "regex",
 *       value: "^[\\w.-]+@[\\w.-]+\\.\\w+$",
 *       errorMessage: "Invalid email format",
 *       level: "error"
 *     }
 *   ]
 * };
 */
export interface FieldConfig {
  columnName: string; // The field name to map to (e.g., "Name", "Email")
  displayName?: string;
  columnRequired: boolean; // Whether the field is mandatory
  type: FieldTypes;
  validations?: Validation[];
  example?: unknown; // Example value for CSV template
}

/**
 * A union type representing the various kinds of field validations.
 *
 * @typedef {RequiredValidation | UniqueValidation | RegexValidation} Validation
 */
type Validation = UniqueValidation | RegexValidation | CustomValidation;

/**
 * Validation to ensure a field's value is unique across the dataset.
 *
 * @typedef {Object} UniqueValidation
 * @property {"unique"} rule - The rule identifier.
 * @property {boolean} [allowEmpty] - If true, empty values will be ignored in the uniqueness check.
 * @property {string} [errorMessage] - Optional custom error message.
 * @property {ErrorLevel} [level] - The severity level of the error.
 */
export type UniqueValidation = {
  rule: "unique";
  allowEmpty?: boolean;
  errorMessage?: string;
  level?: ErrorLevel;
};

/**
 * Validation using a regular expression.
 *
 * @typedef {Object} RegexValidation
 * @property {"regex"} rule - The rule identifier.
 * @property {string} value - The regular expression pattern to test against.
 * @property {string} [flags] - Optional regex flags (e.g., "i" for case-insensitive).
 * @property {string} errorMessage - The error message to display if validation fails.
 * @property {ErrorLevel} [level] - The severity level of the error.
 */
export type RegexValidation = {
  rule: "regex";
  value: string;
  flags?: string;
  errorMessage: string;
  level?: ErrorLevel;
};

/**
 *
 */
/**
 * Represents a custom validation rule.
 *
 * @typedef {object} CustomValidation
 *
 * @property {"custom"} rule - A literal string indicating this is a custom validation rule.
 * @property {(value: unknown, row: Record<string, unknown>) => boolean} validate -
 *   The validation function that takes a value and its associated row, returning true if the value passes the validation, otherwise false.
 * @property {string} errorMessage - The error message to be displayed when the validation fails.
 * @property {ErrorLevel} [level] - An optional property representing the level of the error.
 */
export type CustomValidation = {
  rule: "custom";
  validate: (value: unknown, row: Record<string, unknown>) => boolean;
  errorMessage: string;
  level?: ErrorLevel;
};

/**
 * The severity level for a validation error.
 *
 * @typedef {"info" | "warning" | "error"} ErrorLevel
 */
export type ErrorLevel = "info" | "warning" | "error";

type Info = {
  message: string;
  level: ErrorLevel;
};

export enum ErrorSources {
  Table = "table",
  Row = "row",
}

export type InfoWithSource = Info & {
  source: ErrorSources;
};

export type Meta = { __index: string; __errors?: Error | null };
export type Error = { [key: string]: InfoWithSource };
// export type Errors = { [id: string]: Error };

export enum FieldStatus {
  Unmapped = "Unmapped",
  Custom = "Custom",
  Mapped = "Mapped",
  Ignored = "Ignored",
}

export type FieldMappingItem = {
  id: string;
  csvValue: string;
} & (
  | {
      status: FieldStatus.Mapped;
      mappedValue: string;
      type: FieldTypes;
      displayName?: string;
    }
  | {
      status: FieldStatus.Custom;
      mappedValue: string;
    }
  | { status: FieldStatus.Unmapped | FieldStatus.Ignored }
);

export type CsvColumn = { id: string; column: string };

export type UpdateDataType = (
  rowIndex: string,
  columnName: string,
  value: unknown
) => void;

import { FileText, Upload, X } from "lucide-react";
import * as React from "react";
import Dropzone, {
  type DropzoneProps,
  type FileRejection,
} from "react-dropzone";
import { toast } from "sonner";

import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useControllableState } from "../hooks/use-controllable-state";
import { cn } from "@/lib/utils";
import { formatBytes } from "../utils/helpers";

interface FileUploaderProps extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * Value of the uploader.
   * @type File[]
   * @default undefined
   * @example value={files}
   */
  value?: File[];

  /**
   * Function to be called when the value changes.
   * @type (files: File[]) => void
   * @default undefined
   * @example onValueChange={(files) => setFiles(files)}
   */
  onValueChange?: (files: File[]) => void;

  /**
   * Function to be called when files are uploaded.
   * @type (files: File[]) => Promise<void>
   * @default undefined
   * @example onUpload={(files) => uploadFiles(files)}
   */
  onUpload?: (files: File[]) => Promise<void>;

  /**
   * Progress of the uploaded files.
   * @type Record<string, number> | undefined
   * @default undefined
   * @example progresses={{ "file1.png": 50 }}
   */
  progresses?: Record<string, number>;

  /**
   * Accepted file types for the uploader.
   * @type { [key: string]: string[]}
   * @default
   * ```ts
   * { "image/*": [] }
   * ```
   * @example accept={["image/png", "image/jpeg"]}
   */
  accept?: DropzoneProps["accept"];

  /**
   * Maximum file size for the uploader.
   * @type number | undefined
   * @default 1024 * 1024 * 2 // 2MB
   * @example maxSize={1024 * 1024 * 2} // 2MB
   */
  maxSize?: DropzoneProps["maxSize"];

  /**
   * Maximum number of files for the uploader.
   * @type number | undefined
   * @default 1
   * @example maxFileCount={4}
   */
  maxFileCount?: DropzoneProps["maxFiles"];

  /**
   * Whether the uploader should accept multiple files.
   * @type boolean
   * @default false
   * @example multiple
   */
  multiple?: boolean;

  /**
   * Whether the uploader is disabled.
   * @type boolean
   * @default false
   * @example disabled
   */
  disabled?: boolean;

  /**
   * Class name for the uploader container.
   * @type string | undefined
   * @default undefined
   * @example containerClassName="h-96"
   */
  containerClassName?: string;
}

export function FileUploader(props: FileUploaderProps) {
  const {
    value: valueProp,
    onValueChange,
    onUpload,
    progresses,
    accept = {
      "image/*": [],
    },
    maxSize = 1024 * 1024 * 2,
    maxFileCount = 1,
    multiple = false,
    disabled = false,
    className,
    containerClassName,
    ...dropzoneProps
  } = props;

  const [files, setFiles] = useControllableState({
    prop: valueProp,
    onChange: onValueChange,
  });

  const onDrop = React.useCallback(
    (acceptedFiles: File[], rejectedFiles: FileRejection[]) => {
      if (!multiple && maxFileCount === 1 && acceptedFiles.length > 1) {
        toast.error("Cannot upload more than 1 file at a time");
        return;
      }

      if ((files?.length ?? 0) + acceptedFiles.length > maxFileCount) {
        toast.error(`Cannot upload more than ${maxFileCount} files`);
        return;
      }

      const newFiles = acceptedFiles.map((file) =>
        Object.assign(file, {
          preview: URL.createObjectURL(file),
        })
      );

      const updatedFiles = files ? [...files, ...newFiles] : newFiles;

      setFiles(updatedFiles);

      if (rejectedFiles.length > 0) {
        rejectedFiles.forEach(({ file }) => {
          toast.error(`File ${file.name} was rejected`);
        });
      }

      if (
        onUpload &&
        updatedFiles.length > 0 &&
        updatedFiles.length <= maxFileCount
      ) {
        const target =
          updatedFiles.length > 0 ? `${updatedFiles.length} files` : `file`;

        toast.promise(onUpload(updatedFiles), {
          loading: `Uploading ${target}...`,
          success: () => {
            setFiles([]);
            return `${target} uploaded`;
          },
          error: `Failed to upload ${target}`,
        });
      }
    },

    [files, maxFileCount, multiple, onUpload, setFiles]
  );

  function onRemove(index: number) {
    if (!files) return;
    const newFiles = files.filter((_, i) => i !== index);
    setFiles(newFiles);
    onValueChange?.(newFiles);
  }

  // Revoke preview url when component unmounts
  React.useEffect(() => {
    return () => {
      if (!files) return;
      files.forEach((file) => {
        if (isFileWithPreview(file)) {
          URL.revokeObjectURL(file.preview);
        }
      });
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const isDisabled = disabled || (files?.length ?? 0) >= maxFileCount;

  return (
    <div
      className={cn(
        "flex overflow-hidden relative flex-col gap-6",
        containerClassName
      )}
    >
      <Dropzone
        onDrop={onDrop}
        accept={accept}
        maxSize={maxSize}
        maxFiles={maxFileCount}
        multiple={maxFileCount > 1 || multiple}
        disabled={isDisabled}
      >
        {({ getRootProps, getInputProps, isDragActive }) => (
          <div
            {...getRootProps()}
            className={cn(
              "group relative grid h-52 w-full cursor-pointer place-items-center rounded-lg border-2 border-dashed border-muted-foreground/25 px-5 py-2.5 text-center transition hover:bg-muted/25",
              "ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2",
              isDragActive && "border-muted-foreground/50",
              isDisabled && "pointer-events-none opacity-60",
              className
            )}
            {...dropzoneProps}
          >
            <input {...getInputProps()} />
            {isDragActive ? (
              <div className="flex flex-col gap-4 justify-center items-center sm:px-5">
                <div className="p-3 rounded-full border border-dashed">
                  <Upload
                    className="size-7 text-muted-foreground"
                    aria-hidden="true"
                  />
                </div>
                <p className="font-medium text-muted-foreground">
                  Drop the files here
                </p>
              </div>
            ) : (
              <div className="flex flex-col gap-4 justify-center items-center sm:px-5">
                <div className="p-3 rounded-full border border-dashed">
                  <Upload
                    className="size-7 text-muted-foreground"
                    aria-hidden="true"
                  />
                </div>
                <div className="flex flex-col gap-px">
                  <p className="font-medium text-muted-foreground">
                    Drag {`'n'`} drop files here, or click to select files
                  </p>
                  <p className="text-sm text-muted-foreground/70">
                    You can upload
                    {maxFileCount > 1
                      ? ` ${
                          maxFileCount === Infinity ? "multiple" : maxFileCount
                        }
                      files (up to ${formatBytes(maxSize)} each)`
                      : ` a file with ${formatBytes(maxSize)}`}
                  </p>
                </div>
              </div>
            )}
          </div>
        )}
      </Dropzone>

      {files?.length ? (
        <ScrollArea className="w-full h-fit">
          <div className="flex flex-col gap-4 max-h-48 border">
            {files?.map((file, index) => (
              <FileCard
                key={index}
                file={file}
                onRemove={() => onRemove(index)}
                progress={progresses?.[file.name]}
              />
            ))}
          </div>
        </ScrollArea>
      ) : null}
    </div>
  );
}

interface FileCardProps {
  file: File;
  onRemove: () => void;
  progress?: number;
}

function FileCard({ file, progress, onRemove }: FileCardProps) {
  return (
    <div className="relative flex items-center gap-2.5 p-2 rounded">
      <div className="flex flex-1 gap-2.5">
        {isFileWithPreview(file) ? <FilePreview file={file} /> : null}
        <div className="flex flex-col gap-2 w-full">
          <div className="flex flex-col gap-px">
            <p className="text-sm font-medium line-clamp-1 text-foreground/80">
              {file.name}
            </p>
            <p className="text-xs text-muted-foreground">
              {formatBytes(file.size)}
            </p>
          </div>
          {progress ? <Progress value={progress} /> : null}
        </div>
      </div>
      <div className="flex gap-2 items-center">
        <Button
          type="button"
          variant="outline"
          size="icon"
          className="size-7"
          onClick={onRemove}
        >
          <X className="size-4" aria-hidden="true" />
          <span className="sr-only">Remove file</span>
        </Button>
      </div>
    </div>
  );
}

function isFileWithPreview(file: File): file is File & { preview: string } {
  return "preview" in file && typeof file.preview === "string";
}

interface FilePreviewProps {
  file: File & { preview: string };
}

function FilePreview({ file }: FilePreviewProps) {
  if (file.type.startsWith("image/")) {
    return (
      <img
        src={file.preview}
        alt={file.name}
        width={48}
        height={48}
        loading="lazy"
        className="object-cover rounded-md aspect-square shrink-0"
      />
    );
  }

  return (
    <FileText className="size-10 text-muted-foreground" aria-hidden="true" />
  );
}

import * as React from "react";

/**
 * @see https://github.com/radix-ui/primitives/blob/main/packages/react/use-callback-ref/src/useCallbackRef.tsx
 */

/**
 * A custom hook that converts a callback to a ref to avoid triggering re-renders when passed as a
 * prop or avoid re-executing effects when passed as a dependency
 */
function useCallbackRef<T extends (...args: never[]) => unknown>(
  callback: T | undefined
): T {
  const callbackRef = React.useRef(callback);

  React.useEffect(() => {
    callbackRef.current = callback;
  });

  // https://github.com/facebook/react/issues/19240
  return React.useMemo(
    () => ((...args) => callbackRef.current?.(...args)) as T,
    []
  );
}

export { useCallbackRef };

import * as React from "react";

import { useCallbackRef } from "./use-callback-ref";

/**
 * @see https://github.com/radix-ui/primitives/blob/main/packages/react/use-controllable-state/src/useControllableState.tsx
 */

type UseControllableStateParams<T> = {
  prop?: T | undefined;
  defaultProp?: T | undefined;
  onChange?: (state: T) => void;
};

type SetStateFn<T> = (prevState?: T) => T;

function useControllableState<T>({
  prop,
  defaultProp,
  onChange = () => {},
}: UseControllableStateParams<T>) {
  const [uncontrolledProp, setUncontrolledProp] = useUncontrolledState({
    defaultProp,
    onChange,
  });
  const isControlled = prop !== undefined;
  const value = isControlled ? prop : uncontrolledProp;
  const handleChange = useCallbackRef(onChange);

  const setValue: React.Dispatch<React.SetStateAction<T | undefined>> =
    React.useCallback(
      (nextValue) => {
        if (isControlled) {
          const setter = nextValue as SetStateFn<T>;
          const value =
            typeof nextValue === "function" ? setter(prop) : nextValue;
          if (value !== prop) handleChange(value as T);
        } else {
          setUncontrolledProp(nextValue);
        }
      },
      [isControlled, prop, setUncontrolledProp, handleChange]
    );

  return [value, setValue] as const;
}

function useUncontrolledState<T>({
  defaultProp,
  onChange,
}: Omit<UseControllableStateParams<T>, "prop">) {
  const uncontrolledState = React.useState<T | undefined>(defaultProp);
  const [value] = uncontrolledState;
  const prevValueRef = React.useRef(value);
  const handleChange = useCallbackRef(onChange);

  React.useEffect(() => {
    if (prevValueRef.current !== value) {
      handleChange(value as T);
      prevValueRef.current = value;
    }
  }, [value, prevValueRef, handleChange]);

  return uncontrolledState;
}

export { useControllableState };

import { FileUploader } from "../components/file-uploader";
import { Button } from "@/components/ui/button";
import { DialogFooter } from "@/components/ui/dialog";
import { Separator } from "@/components/ui/separator";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { parseCsv } from "../utils/csv-parse";
import { Check, Loader, Download } from "lucide-react";
import { useState } from "react";
import { toast } from "sonner";
import { FlowSteps } from "..";
import { FieldConfig, StepItems } from "../types";
import { formatBytes, toHeaderCase } from "../utils/helpers";
import {
  generateCsvTemplate,
  downloadCsvTemplate,
} from "../utils/template-generator";

type Props = {
  fields: FieldConfig[];
  maxRows: number;
  setStep: React.Dispatch<React.SetStateAction<FlowSteps>>;
  maxFileSize: number;
  showTemplateDownload?: boolean;
};

function UploadStep(props: Props) {
  const {
    fields,
    maxRows,
    setStep,
    maxFileSize,
    showTemplateDownload = true,
  } = props;
  const [processing, setProcessing] = useState(false);

  const [files, setFiles] = useState<File[]>([]);

  function handleDownloadTemplate() {
    if (!fields || fields.length === 0) {
      toast.error("No field configurations available for template generation");
      return;
    }

    try {
      const csvContent = generateCsvTemplate(fields, true);
      downloadCsvTemplate(csvContent, "csv-template.csv");
      toast.success("Template downloaded successfully!");
    } catch (error) {
      toast.error("Failed to generate template");
      console.error("Template generation error:", error);
    }
  }

  async function onNextStepClick() {
    setProcessing(true);
    try {
      const parsedResult = await parseCsv({ file: files[0], limit: maxRows });
      // await new Promise<void>((resolve) => setTimeout(resolve, 10000));
      toast.success("CSV parsed successfully!");
      setStep({
        step: StepItems.Map,
        ...parsedResult,
      });
    } catch (error) {
      toast.error(error instanceof Error ? error.message : "Parsing failed.");
    } finally {
      setProcessing(false);
    }
  }

  return (
    <div className="flex flex-col gap-4 h-full">
      <div className="grid overflow-y-auto grid-cols-1 gap-6 grow md:grid-cols-3">
        <div className="col-span-2 h-full">
          <FileUploader
            maxFileCount={1}
            maxSize={maxFileSize}
            onValueChange={setFiles}
            containerClassName="h-full"
            className="flex-1"
            accept={{
              "text/csv": [".csv"],
            }}
          />
        </div>
        <div>
          <div className="flex flex-col gap-2 mb-4">
            <p className="p-2 text-sm text-center rounded-lg text-muted-foreground bg-muted">
              Max file size: {formatBytes(maxFileSize)}, Max rows: {maxRows}
            </p>
            {showTemplateDownload && (
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={handleDownloadTemplate}
                className="flex gap-2 items-center w-full"
              >
                <Download className="size-4" />
                Download Template
              </Button>
            )}
          </div>
          <Separator className="my-2" />
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Expected Column</TableHead>
                <TableHead>Expected Type</TableHead>
                <TableHead>Required</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {fields.map((field) => (
                <TableRow key={field.columnName}>
                  <TableCell>{field.displayName || field.columnName}</TableCell>
                  <TableCell>{toHeaderCase(field.type)}</TableCell>
                  <TableCell>
                    {field.columnRequired ? <Check size={16} /> : null}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </div>

      <DialogFooter>
        {processing && (
          <p className="flex gap-2 items-center mr-4 text-sm text-muted-foreground">
            <Loader className="animate-spin size-4" /> Processing...
          </p>
        )}
        <Button
          onClick={onNextStepClick}
          disabled={files.length === 0 || processing}
        >
          Next
        </Button>
      </DialogFooter>
    </div>
  );
}

