import { CsvColumn } from "../types";
import { nanoid } from "nanoid";
import Papa from "papaparse";

interface ParseCsvArgs {
  file: File;
  limit: number;
  showEmptyFields?: boolean;
  config?: Papa.ParseConfig;
}

export async function parseCsv(
  args: ParseCsvArgs
): Promise<{ data: Record<string, string>[]; columns: CsvColumn[] }> {
  const { file, config = {}, limit, showEmptyFields = true } = args;

  return new Promise((resolve, reject) => {
    const parsedData: Record<string, string>[] = [];
    let columns: string[] = [];
    let rowCount = 0;
    let abortedDueToLimit = false;

    Papa.parse<Record<string, string>>(file, {
      ...config,
      header: true,
      dynamicTyping: false,
      skipEmptyLines: true,
      beforeFirstChunk: (chunk) => {
        const { updatedChunk, extractedColumns } = processFirstChunk(
          chunk,
          showEmptyFields
        );
        columns = extractedColumns;
        return updatedChunk;
      },
      step: (results, parser) => {
        if (rowCount < limit) {
          parsedData.push(results.data);
          rowCount++;
        } else {
          // Mark that we exceeded the row limit and abort the parsing.
          abortedDueToLimit = true;
          parser.abort();
        }
      },
      complete: () => {
        if (abortedDueToLimit) {
          reject(
            new Error(
              `ðŸš¨ Row limit exceeded: Please upload a CSV file with a maximum of ${limit} rows.`
            )
          );
        } else {
          resolve({
            data: parsedData,
            columns: columns.map((v) => ({ id: nanoid(), column: v })),
          });
        }
      },
      error: (error) => reject(new Error(`Parsing failed: ${error.message}`)),
    });

    function processFirstChunk(
      chunk: string,
      showEmptyFields: boolean
    ): { updatedChunk: string; extractedColumns: string[] } {
      const parsed = Papa.parse<string[]>(chunk, {
        header: false,
        skipEmptyLines: true,
      });
      const rows = parsed.data;
      const firstRow = rows[0] || [];

      const processedColumns = firstRow
        .map((col, i) => {
          if (
            !showEmptyFields &&
            !rows.slice(1).some((row) => row[i]?.trim())
          ) {
            return null;
          }
          return col.trim() || `Field ${i + 1}`;
        })
        .filter(Boolean) as string[];

      // Replace the first row with the processed columns.
      rows[0] = processedColumns;
      return {
        updatedChunk: Papa.unparse(rows),
        extractedColumns: processedColumns,
      };
    }
  });
}

import { z, ZodTypeAny } from "zod";
import {
  ErrorSources,
  FieldConfig,
  FieldMappingItem,
  FieldStatus,
  Meta,
  Error as FieldError,
  UniqueValidation,
  CustomValidation,
  RegexValidation,
  InfoWithSource,
  ErrorLevel,
} from "../types";
import { nanoid } from "nanoid";
import { isValid } from "date-fns";

/**
 * Maps a single CSV row using the provided field mappings.
 */
export function mapCsvRow(
  row: Record<string, unknown>,
  fieldMappings: FieldMappingItem[]
): Record<string, unknown> {
  const mappedRow: Record<string, unknown> = {};
  fieldMappings.forEach((field) => {
    if (
      field.status === FieldStatus.Mapped ||
      field.status === FieldStatus.Custom
    ) {
      mappedRow[field.mappedValue] = row[field.csvValue];
    }
  });
  return mappedRow;
}

/**
 * Returns a Zod schema for a field based on its type and requirements.
 * Handles both base type validation and required field validation.
 */
function getFieldSchema(field: FieldConfig): ZodTypeAny {
  let schema: ZodTypeAny;

  // Create base schema based on field type
  switch (field.type) {
    case "number":
      schema = z.preprocess((val) => {
        if (typeof val === "string") {
          if (!val.trim()) return undefined;
          const num = Number(val);
          return isNaN(num) ? val : num;
        }
        return val;
      }, z.number().optional());
      break;
    case "boolean":
      schema = z.preprocess((val) => {
        if (typeof val === "string") {
          if (!val.trim()) return undefined;
          const lower = val.toLowerCase();
          if (lower === "true") return true;
          if (lower === "false") return false;
        }
        return val;
      }, z.boolean().optional());
      break;
    case "date":
      schema = z.preprocess(
        (val) => {
          if (typeof val === "string") {
            if (!val.trim()) return undefined;
            return val;
          }
          return val;
        },
        z
          .string()
          .refine((val) => isValid(new Date(val)), {
            message: "Must be a valid date",
          })
          .optional()
      );
      break;
    case "email":
      schema = z.preprocess((val) => {
        if (typeof val === "string") {
          if (!val.trim()) return undefined;
          return val.trim();
        }
        return val;
      }, z.string().email().optional());
      break;
    default:
      schema = z.string().optional();
  }

  // Add required validation if needed
  if (field.columnRequired) {
    if (schema instanceof z.ZodString) {
      schema = schema.min(1, { message: "This field is required" });
    } else {
      schema = schema.refine(
        (val) => val !== undefined && val !== null && val !== "",
        { message: "This field is required" }
      );
    }
  }

  return schema;
}

/**
 * Helper function to update an error for a field in an error object.
 * Only updates if the new error's level is higher than the current one.
 */
function updateError(
  fieldName: string,
  newError: InfoWithSource,
  errors: FieldError
) {
  const priority: Record<ErrorLevel, number> = {
    info: 1,
    warning: 2,
    error: 3,
  };
  const current = errors[fieldName];
  if (!current || priority[newError.level] > priority[current.level]) {
    errors[fieldName] = newError;
  }
}

/**
 * Applies validations to the mapped data using Zod for type validations.
 * After parsing each row with the generated row schema, any Zod errors are
 * converted to your expected error format. Then, we run custom (row-level)
 * validations, regex validations, and table-level unique validations.
 *
 * The `fieldMappings` parameter is used to determine if a given field is
 * actually mapped. If a field isn't mapped (status Unmapped/ Ignored),
 * unique validations for that field are skipped.
 */
export async function addErrorsToData(
  data: Record<string, unknown>[],
  fields: FieldConfig[],
  fieldMappings: FieldMappingItem[]
): Promise<(Record<string, unknown> & Meta)[]> {
  function isFieldMapped(field: FieldConfig) {
    return fieldMappings.some(
      (mapping) =>
        (mapping.status === FieldStatus.Mapped ||
          mapping.status === FieldStatus.Custom) &&
        mapping.mappedValue === field.columnName
    );
  }

  const processedRows = data.map((row) => {
    const newRow: Record<string, unknown> = { ...row };
    const errors: FieldError = {};

    // Type Validation
    fields.forEach((field) => {
      const value = row[field.columnName];
      const schema = getFieldSchema(field);
      const result = schema.safeParse(value);
      if (result.success) {
        newRow[field.columnName] = result.data;
      } else {
        newRow[field.columnName] = value;
        updateError(
          field.columnName,
          {
            message: result.error.issues[0].message,
            level: "error",
            source: ErrorSources.Row,
          },
          errors
        );
      }
    });

    // Custom and Regex Validations (only if field is mapped)
    fields.forEach((field) => {
      if (!isFieldMapped(field)) return;

      // Skip validations if the field is empty and not required.
      const fieldValue = newRow[field.columnName];
      const isEmpty =
        fieldValue === undefined || fieldValue === null || fieldValue === "";
      if (isEmpty && !field.columnRequired) return;

      // Custom Validations.
      const customValidations = field.validations?.filter(
        (v) => v.rule === "custom"
      ) as CustomValidation[] | undefined;

      if (customValidations) {
        customValidations.forEach((validation) => {
          if (!validation.validate(newRow[field.columnName], newRow)) {
            updateError(
              field.columnName,
              {
                message: validation.errorMessage,
                level: validation.level || "error",
                source: ErrorSources.Row,
              },
              errors
            );
          }
        });
      }

      // Regex Validations.
      const regexValidations = field.validations?.filter(
        (v) => v.rule === "regex"
      ) as RegexValidation[] | undefined;

      if (regexValidations) {
        regexValidations.forEach((validation) => {
          const regex = new RegExp(validation.value, validation.flags);
          if (!regex.test(String(newRow[field.columnName] || ""))) {
            updateError(
              field.columnName,
              {
                message: validation.errorMessage,
                level: validation.level || "error",
                source: ErrorSources.Row,
              },
              errors
            );
          }
        });
      }
    });

    return {
      ...newRow,
      __index: (row.__index as string) || nanoid(),
      __errors: Object.keys(errors).length > 0 ? errors : null,
    };
  });

  let result: (Record<string, unknown> & Meta)[] = processedRows;

  // Unique Validation
  fields.forEach((field) => {
    const uniqueValidation = field.validations?.find(
      (v) => v.rule === "unique"
    ) as UniqueValidation | undefined;

    if (uniqueValidation) {
      const isMapped = isFieldMapped(field);
      if (!isMapped) return;

      const values = result.map((entry) => entry[field.columnName]);
      const seen = new Set();
      const duplicates = new Set();
      values.forEach((val) => {
        if (uniqueValidation.allowEmpty && (val === undefined || val === ""))
          return;
        if (seen.has(val)) {
          duplicates.add(val);
        } else {
          seen.add(val);
        }
      });

      result = result.map((entry) => {
        if (duplicates.has(entry[field.columnName])) {
          const currentErrors = entry.__errors || ({} as FieldError);
          updateError(
            field.columnName,
            {
              message: uniqueValidation.errorMessage || "Field must be unique",
              level: uniqueValidation.level || "error",
              source: ErrorSources.Table,
            },
            currentErrors
          );
          return { ...entry, __errors: currentErrors };
        }
        return entry;
      });
    }
  });

  return result;
}

export function formatBytes(
  bytes: number,
  opts: {
    decimals?: number;
    sizeType?: "accurate" | "normal";
  } = {}
) {
  const { decimals = 0, sizeType = "normal" } = opts;

  const sizes = ["Bytes", "KB", "MB", "GB", "TB"];
  const accurateSizes = ["Bytes", "KiB", "MiB", "GiB", "TiB"];
  if (bytes === 0) return "0 Byte";
  const i = Math.floor(Math.log(bytes) / Math.log(1024));
  return `${(bytes / Math.pow(1024, i)).toFixed(decimals)} ${
    sizeType === "accurate" ? accurateSizes[i] ?? "Bytes" : sizes[i] ?? "Bytes"
  }`;
}

export function toHeaderCase(str: string = ""): string {
  if (!str) return "";

  return String(str)
    .replace(/^[^A-Za-z0-9]*|[^A-Za-z0-9]*$/g, "")
    .replace(/([a-z])([A-Z])/g, (_, a, b) => `${a}_${b.toLowerCase()}`)
    .replace(/[^A-Za-z0-9]+|_+/g, " ")
    .toLowerCase()
    .replace(
      /( ?)(\w+)( ?)/g,
      (_, a, b, c) => a + b.charAt(0).toUpperCase() + b.slice(1) + c
    );
}

import { FieldConfig } from "../types";

/**
 * Generates a CSV template with headers and optional example data
 * @param fields - Array of field configurations
 * @param includeExampleRow - Whether to include an example row with sample data
 * @returns CSV content as a string
 */
export function generateCsvTemplate(
  fields: FieldConfig[],
  includeExampleRow: boolean = true
): string {
  // Generate headers using displayName or columnName
  const headers = fields.map((field) => field.displayName || field.columnName);

  // Create CSV content
  let csvContent = headers.join(",") + "\n";

  if (includeExampleRow) {
    // Generate example row with example values or default values
    const exampleRow = fields.map((field) => {
      if (field.example !== undefined && field.example !== null) {
        // Use provided example value, convert to string and escape if needed
        const exampleString = convertToString(field.example);
        return escapeCsvValue(exampleString);
      }

      // Generate default example based on field type
      return generateDefaultExample(field.type);
    });

    csvContent += exampleRow.join(",") + "\n";
  }

  return csvContent;
}

/**
 * Converts an unknown value to a string for CSV output
 * @param value - The value to convert
 * @returns String representation of the value
 */
function convertToString(value: unknown): string {
  if (typeof value === "string") {
    return value;
  }
  if (typeof value === "number") {
    return value.toString();
  }
  if (typeof value === "boolean") {
    return value.toString();
  }
  if (value instanceof Date) {
    return value.toISOString().split("T")[0]; // YYYY-MM-DD format
  }
  if (value === null || value === undefined) {
    return "";
  }
  // For objects, arrays, etc., convert to JSON string
  return JSON.stringify(value);
}

/**
 * Generates a default example value based on field type
 * @param type - The field type
 * @returns A default example value
 */
function generateDefaultExample(type: string): string {
  switch (type) {
    case "string":
      return "Example Text";
    case "number":
      return "123";
    case "boolean":
      return "true";
    case "email":
      return "example@email.com";
    case "date":
      return "2024-01-01";
    default:
      return "Example Value";
  }
}

/**
 * Escapes CSV values that contain commas, quotes, or newlines
 * @param value - The value to escape
 * @returns The escaped value
 */
function escapeCsvValue(value: string): string {
  // If the value contains comma, quote, or newline, wrap it in quotes and escape internal quotes
  if (value.includes(",") || value.includes('"') || value.includes("\n")) {
    return `"${value.replace(/"/g, '""')}"`;
  }
  return value;
}

/**
 * Downloads a CSV file with the given content and filename
 * @param content - The CSV content
 * @param filename - The filename for the download (default: "template.csv")
 */
export function downloadCsvTemplate(
  content: string,
  filename: string = "template.csv"
): void {
  // Create a blob with the CSV content
  const blob = new Blob([content], { type: "text/csv;charset=utf-8;" });

  // Create a download link
  const link = document.createElement("a");
  const url = URL.createObjectURL(blob);

  link.setAttribute("href", url);
  link.setAttribute("download", filename);
  link.style.visibility = "hidden";

  // Add to DOM, click, and remove
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);

  // Clean up the URL object
  URL.revokeObjectURL(url);
}

import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { addErrorsToData } from "../../utils/map-utils";
import { cn } from "@/lib/utils";
import { TooltipProvider } from "@/components/ui/tooltip";
import {
  ColumnDef,
  ColumnFiltersState,
  flexRender,
  getCoreRowModel,
  getSortedRowModel,
  RowData,
  useReactTable,
} from "@tanstack/react-table";
import { Loader, Trash } from "lucide-react";
import { useMemo, useRef, useState } from "react";
import { toast } from "sonner";
import {
  CustomFieldReturnType,
  FieldConfig,
  FieldMappingItem,
  FieldStatus,
  Meta,
  UpdateDataType,
} from "../../types";
import { DataTableColumnHeader } from "./data-table-header";
import { EditableCell } from "./editable-cell";
import { TableBody } from "./table-body";

declare module "@tanstack/react-table" {
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  interface TableMeta<TData extends RowData> {
    updateData: UpdateDataType;
  }
}

type ReviewStepProps = {
  fields: FieldConfig[];
  data: (Record<string, unknown> & Meta)[];
  fieldMappings: FieldMappingItem[];
  enableCustomFields?: boolean;
  customFieldReturnType?: CustomFieldReturnType;
  onImport: (data: Record<string, unknown>[]) => void;
  handleCloseDialog: () => void;
};

export function ReviewStep(props: ReviewStepProps) {
  const {
    data,
    fieldMappings,
    fields,
    enableCustomFields,
    customFieldReturnType,
    onImport,
    handleCloseDialog,
  } = props;

  const [rowData, setRowData] =
    useState<(Record<string, unknown> & Meta)[]>(data);
  const [filterErrors, setFilterErrors] = useState<boolean>(false);
  const [rowSelection, setRowSelection] = useState<Record<string, boolean>>({});
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);
  const [isImporting, setIsImporting] = useState<boolean>(false);
  const tableContainerRef = useRef<HTMLDivElement>(null);

  const columns: ColumnDef<Record<string, unknown> & Meta>[] = useMemo<
    ColumnDef<Record<string, unknown> & Meta>[]
  >(() => {
    const mappingColumns = fieldMappings.reduce<
      ColumnDef<Record<string, unknown> & Meta>[]
    >((acc, mapping) => {
      if (
        mapping.status === FieldStatus.Mapped ||
        mapping.status === FieldStatus.Custom
      ) {
        acc.push({
          id: mapping.id,
          header: ({ column }) => (
            <div className="pl-2 w-full">
              <DataTableColumnHeader
                column={column}
                title={
                  mapping.status === FieldStatus.Mapped
                    ? (mapping.displayName ?? mapping.mappedValue)
                    : mapping.mappedValue
                }
              />
            </div>
          ),
          enableSorting: true,
          accessorKey: mapping.mappedValue,
          size: 180,
          minSize: 100,
          cell: ({ getValue, row, table }) => (
            <EditableCell
              initialValue={getValue()}
              row={row}
              updateData={table.options.meta?.updateData}
              columnName={mapping.mappedValue}
              columnId={mapping.id}
            />
          ),
        });
      }
      return acc;
    }, []);

    return [
      {
        id: "select",
        header: ({ table }) => (
          <Checkbox
            checked={
              table.getIsAllPageRowsSelected() ||
              (table.getIsSomePageRowsSelected() && "indeterminate")
            }
            onCheckedChange={(value) =>
              table.toggleAllPageRowsSelected(!!value)
            }
            aria-label="Select all"
          />
        ),
        cell: ({ row }) => (
          <div className="px-2 py-1">
            <Checkbox
              checked={row.getIsSelected()}
              onCheckedChange={(value) => row.toggleSelected(!!value)}
              aria-label="Select row"
            />
          </div>
        ),
        size: 48,
        minSize: 48,
        enableResizing: false,
      },
      ...mappingColumns,
    ];
  }, [fieldMappings]);

  const filteredData = useMemo(() => {
    if (!filterErrors) return rowData;
    return rowData.filter(
      (row) =>
        row.__errors &&
        Object.values(row.__errors).some((err) => err.level === "error")
    );
  }, [rowData, filterErrors]);

  const table = useReactTable({
    data: filteredData,
    columns,
    getRowId: (row) => row.__index,
    defaultColumn: {
      minSize: 50,
      maxSize: 600,
    },
    columnResizeMode: "onChange",
    state: {
      rowSelection,
      columnFilters,
    },
    onColumnFiltersChange: setColumnFilters,
    onRowSelectionChange: setRowSelection,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    meta: {
      updateData: async (rowIndex, columnName, value) => {
        const newRowData = rowData.map((row) => {
          if (row.__index === rowIndex) {
            return {
              ...row,
              [columnName]: value,
            };
          }
          return row;
        });
        const newDataWithErrors = await addErrorsToData(
          newRowData,
          fields,
          fieldMappings
        );
        setRowData(newDataWithErrors);
      },
    },
  });

  const selectedRowIds = useMemo(
    () => Object.keys(rowSelection).filter((id) => rowSelection[id]),
    [rowSelection]
  );

  const deleteSelectedRows = async () => {
    const newData = rowData.filter(
      (row) => !selectedRowIds.includes(row.__index)
    );
    const newDataWithErrors = await addErrorsToData(
      newData,
      fields,
      fieldMappings
    );

    setRowData(newDataWithErrors);
    setRowSelection({});
  };

  const handleImport = async () => {
    const hasErrors = rowData.some(
      (row) =>
        row.__errors &&
        Object.values(row.__errors).some((err) => err.level === "error")
    );

    if (hasErrors) {
      toast.error(
        "There are rows with errors. Please fix them before importing."
      );
      return;
    }

    setIsImporting(true);

    try {
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      let cleanedData = rowData.map(({ __index, __errors, ...rest }) => rest);

      if (enableCustomFields) {
        const customMappings = fieldMappings.filter(
          (mapping) => mapping.status === FieldStatus.Custom
        );
        if (customFieldReturnType === "object") {
          cleanedData = cleanedData.map((row) => {
            const customFields: Record<string, unknown> = {};
            for (const mapping of customMappings) {
              const key = mapping.mappedValue;
              if (key in row) {
                customFields[key] = row[key];
                delete row[key];
              }
            }
            return { ...row, customFields };
          });
        } else if (customFieldReturnType === "json") {
          cleanedData = cleanedData.map((row) => {
            const customFields: Record<string, unknown> = {};
            for (const mapping of customMappings) {
              const key = mapping.mappedValue;
              if (key in row) {
                customFields[key] = row[key];
                delete row[key];
              }
            }
            return { ...row, customFields: JSON.stringify(customFields) };
          });
        }
      }

      onImport(cleanedData);
      handleCloseDialog();
      toast.success("Data imported successfully!");
      setIsImporting(false);
    } catch (error) {
      toast.error("Error importing data: " + (error as Error)?.message);
      setIsImporting(false);
    }
  };

  const columnSizeVars = useMemo(() => {
    const headers = table.getFlatHeaders();
    const colSizes: { [key: string]: number } = {};
    for (let i = 0; i < headers.length; i++) {
      const header = headers[i]!;
      colSizes[`--header-${header.id}-size`] = header.getSize();
      colSizes[`--col-${header.column.id}-size`] = header.column.getSize();
    }
    return colSizes;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [table.getState().columnSizingInfo, table.getState().columnSizing]);

  return (
    <TooltipProvider>
      <div className="flex flex-col h-full">
        {/* Top controls: Filter and Delete */}
        <div className="flex flex-wrap gap-4 justify-between items-center mb-4">
          <div>
            <h2 className="text-2xl font-semibold">Review Your Mapped Data</h2>
            <p className="text-sm text-muted-foreground">
              Please verify the data below. You can update any field, select
              rows to discard, or filter to display only rows with errors before
              finalizing the import.
            </p>
          </div>
          <div className="flex gap-4 items-center">
            <Label className="flex gap-2 items-center">
              <span>Show rows with errors</span>
              <Switch
                checked={filterErrors}
                onCheckedChange={(e: boolean) => {
                  setFilterErrors(e);
                  setRowSelection({});
                }}
              />
            </Label>

            <Button
              variant="ghost"
              size="sm"
              onClick={deleteSelectedRows}
              className="text-destructive hover:text-destructive hover:bg-destructive/5"
              disabled={!selectedRowIds.length}
            >
              <Trash className="size-3" />
              Delete Selected Rows
            </Button>
          </div>
        </div>

        {/* Table container */}
        <div
          className="overflow-auto relative w-full text-sm rounded-md border grow scrollbar-thin scrollbar-thumb-muted-foreground/15 scrollbar-track-muted"
          ref={tableContainerRef}
        >
          <table
            className="grid w-full text-sm caption-bottom"
            style={columnSizeVars}
          >
            <thead className="[&_tr]:border-b bg-background z-10 grid sticky top-0">
              {table.getHeaderGroups().map((headerGroup) => (
                <tr
                  key={headerGroup.id}
                  className="border-b transition-colors hover:bg-muted/50 data-[state=selected]:bg-muted flex w-full"
                >
                  {headerGroup.headers.map((header) => (
                    <th
                      style={{
                        // width: header.getSize(),
                        width: `calc(var(--header-${header?.id}-size) * 1px)`,
                      }}
                      className="h-10 px-2 text-left align-middle font-medium text-muted-foreground [&:has([role=checkbox])]:pr-0 [&>[role=checkbox]]:translate-y-[2px] flex items-center relative group/th"
                      key={header.id}
                    >
                      {header.isPlaceholder
                        ? null
                        : flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                      <div
                        className={cn({
                          "absolute top-0 right-0 cursor-col-resize w-1.5 h-full bg-muted-foreground/30 hover:bg-muted-foreground/50 rounded-lg opacity-0 group-hover/th:opacity-100 transition-opacity":
                            header.column.getCanResize(),
                        })}
                        {...{
                          onDoubleClick: () => header.column.resetSize(),
                          onMouseDown: header.getResizeHandler(),
                          onTouchStart: header.getResizeHandler(),
                        }}
                      />
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <TableBody
              table={table}
              tableContainerRef={tableContainerRef}
              columnsLength={columns.length}
            />
          </table>
        </div>

        {/* Continue button */}
        <div className="flex justify-between items-center mt-4">
          <div className="text-sm text-muted-foreground">
            Total Rows: {rowData.length}
            {selectedRowIds.length > 0 && (
              <span className="ml-4">
                Selected Rows: {selectedRowIds.length}
              </span>
            )}
          </div>

          <div className="flex justify-end">
            {isImporting && (
              <p className="flex gap-2 items-center mr-4 text-sm text-muted-foreground">
                <Loader className="animate-spin size-4" /> Processing...
              </p>
            )}
            <Button disabled={isImporting} onClick={handleImport}>
              Import
            </Button>
          </div>
        </div>
      </div>
    </TooltipProvider>
  );
}

import { SelectIcon } from "@radix-ui/react-select";
import type { Column } from "@tanstack/react-table";
import { ArrowDown, ArrowUp, ChevronsUpDown } from "lucide-react";

import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
} from "@/components/ui/select";
import { cn } from "@/lib/utils";

interface DataTableColumnHeaderProps<TData, TValue>
  extends React.HTMLAttributes<HTMLDivElement> {
  column: Column<TData, TValue>;
  title: string;
}

export function DataTableColumnHeader<TData, TValue>(
  props: DataTableColumnHeaderProps<TData, TValue>
) {
  const { column, title, className } = props;

  if (!column.getCanSort() && !column.getCanHide()) {
    return <div className={cn(className)}>{title}</div>;
  }

  const ascValue = `${column.id}-asc`;
  const descValue = `${column.id}-desc`;
  const hideValue = `${column.id}-hide`;

  return (
    <div className={cn("flex items-center gap-2", className)}>
      <Select
        value={
          column.getIsSorted() === "desc"
            ? descValue
            : column.getIsSorted() === "asc"
            ? ascValue
            : undefined
        }
        onValueChange={(value) => {
          if (value === ascValue) column.toggleSorting(false);
          else if (value === descValue) column.toggleSorting(true);
          else if (value === hideValue) column.toggleVisibility(false);
        }}
      >
        <SelectTrigger
          aria-label={
            column.getIsSorted() === "desc"
              ? "Sorted descending. Click to sort ascending."
              : column.getIsSorted() === "asc"
              ? "Sorted ascending. Click to sort descending."
              : "Not sorted. Click to sort ascending."
          }
          className="-ml-3 h-8 max-w-fit border-none text-xs hover:bg-accent hover:text-accent-foreground data-[state=open]:bg-accent [&>svg:last-child]:hidden"
        >
          {/* <div className="flex-1 min-w-0"> */}
          <span className="flex-1 truncate">{title}</span>
          {/* </div> */}
          <SelectIcon asChild className="shrink-0">
            {column.getCanSort() && column.getIsSorted() === "desc" ? (
              <ArrowDown className="ml-2.5 size-3" aria-hidden="true" />
            ) : column.getIsSorted() === "asc" ? (
              <ArrowUp className="ml-2.5 size-3" aria-hidden="true" />
            ) : (
              <ChevronsUpDown className="ml-2.5 size-3" aria-hidden="true" />
            )}
          </SelectIcon>
        </SelectTrigger>
        <SelectContent align="start">
          {column.getCanSort() && (
            <>
              <SelectItem value={ascValue}>
                <span className="flex items-center">
                  <ArrowUp
                    className="mr-2 size-3.5 text-muted-foreground/70"
                    aria-hidden="true"
                  />
                  Asc
                </span>
              </SelectItem>
              <SelectItem value={descValue}>
                <span className="flex items-center">
                  <ArrowDown
                    className="mr-2 size-3.5 text-muted-foreground/70"
                    aria-hidden="true"
                  />
                  Desc
                </span>
              </SelectItem>
            </>
          )}
          {/* {column.getCanHide() && (
            <SelectItem value={hideValue}>
              <span className="flex items-center">
                <EyeOff
                  className="mr-2 size-3.5 text-muted-foreground/70"
                  aria-hidden="true"
                />
                Hide
              </span>
            </SelectItem>
          )} */}
        </SelectContent>
      </Select>
    </div>
  );
}

import { Row } from "@tanstack/react-table";
import { Meta, UpdateDataType } from "../../types";
import { useEffect, useState } from "react";
import { cn } from "@/lib/utils";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { toHeaderCase } from "../../utils/helpers";

type Props = {
  initialValue: unknown;
  updateData?: UpdateDataType;
  row: Row<Record<string, unknown> & Meta>;
  columnId: string;
  columnName: string;
};

export function EditableCell(props: Props) {
  const { initialValue, updateData, row, columnId, columnName } = props;

  const [value, setValue] = useState(initialValue);

  const onBlur = () => {
    if (value !== initialValue) {
      updateData?.(row.original.__index, columnName, value);
    }
  };

  useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  const hasError = row.original.__errors?.[columnName];
  const isError = !!hasError && hasError.level === "error";
  const isWarning = !!hasError && hasError.level === "warning";
  const isInfo = !!hasError && hasError.level === "info";

  const displayValue = typeof value === "boolean" ? String(value) : value;

  return (
    <div
      className={cn("border border-transparent", {
        "border-l-destructive border-l-2 bg-destructive/5": isError,
        "border-l-yellow-500 border-l-2 bg-yellow-500/5": isWarning,
        "border-l-blue-500 border-l-2 bg-blue-500/5": isInfo,
      })}
    >
      {hasError ? (
        <Tooltip>
          <TooltipTrigger asChild>
            <input
              id={`${columnId}-${row.original.__index}}`}
              className="w-full px-3 py-1 leading-normal align-middle bg-transparent text-text focus:outline-none focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring"
              value={displayValue ? String(displayValue) : ""}
              onChange={(e) => setValue(e.target.value)}
              onBlur={onBlur}
            />
          </TooltipTrigger>
          <TooltipContent>
            <p>
              <span className="font-medium">
                {toHeaderCase(hasError.level)}
              </span>
              : {hasError.message}
            </p>
          </TooltipContent>
        </Tooltip>
      ) : (
        <input
          id={`${columnId}-${row.original.__index}}`}
          className="w-full px-3 py-1 leading-normal align-middle bg-transparent text-text focus:outline-none focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring"
          value={displayValue ? String(displayValue) : ""}
          onChange={(e) => setValue(e.target.value)}
          onBlur={onBlur}
        />
      )}
    </div>
  );
}

import { flexRender, Row } from "@tanstack/react-table";
import { VirtualItem, Virtualizer } from "@tanstack/react-virtual";
import { Meta } from "../../types";

type TableBodyRowProps = {
  row: Row<Record<string, unknown> & Meta>;
  virtualRow: VirtualItem;
  rowVirtualizer: Virtualizer<HTMLDivElement, HTMLTableRowElement>;
};

export function TableBodyRow(props: TableBodyRowProps) {
  const { row, virtualRow, rowVirtualizer } = props;

  return (
    <tr
      data-index={virtualRow.index}
      ref={(node) => rowVirtualizer.measureElement(node)}
      style={{
        transform: `translateY(${virtualRow.start}px)`,
      }}
      key={row.id}
      className="border-b transition-colors hover:bg-muted/50 data-[state=selected]:bg-muted flex absolute w-full"
      data-state={row.getIsSelected() && "selected"}
    >
      {row.getVisibleCells().map((cell) => (
        <td
          className="align-middle [&:has([role=checkbox])]:pr-0 [&>[role=checkbox]]:translate-y-[2px]"
          key={cell.id}
          style={{
            // width: `${cell.column.getSize()}px`,
            minWidth: `${cell.column.columnDef.minSize}px`,
            width: `calc(var(--col-${cell.column.id}-size) * 1px)`,
          }}
        >
          {flexRender(cell.column.columnDef.cell, cell.getContext())}
        </td>
      ))}
    </tr>
  );
}

import { Table } from "@tanstack/react-table";
import { Meta } from "../../types";
import { useVirtualizer } from "@tanstack/react-virtual";
import { useCallback, useEffect } from "react";
import { TableBodyRow } from "./table-body-row";

type TableBodyProps = {
  table: Table<Record<string, unknown> & Meta>;
  tableContainerRef: React.RefObject<HTMLDivElement>;
  columnsLength: number;
};

export function TableBody(props: TableBodyProps) {
  const { table, tableContainerRef, columnsLength } = props;

  const { rows } = table.getRowModel();

  const rowVirtualizer = useVirtualizer<HTMLDivElement, HTMLTableRowElement>({
    count: rows.length,
    estimateSize: useCallback(() => 34, []),
    getScrollElement: () => tableContainerRef.current,
    measureElement:
      typeof window !== "undefined" &&
      navigator.userAgent.indexOf("Firefox") === -1
        ? (element) => element?.getBoundingClientRect().height
        : undefined,
  });

  useEffect(() => {
    if (tableContainerRef.current) {
      rowVirtualizer.measure();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [tableContainerRef.current]);

  const virtualizedRowItems = rowVirtualizer.getVirtualItems();

  return (
    <tbody
      className="[&_tr:last-child]:border-0 relative grid"
      style={{
        height: `${rowVirtualizer.getTotalSize()}px`,
      }}
    >
      {virtualizedRowItems.length ? (
        virtualizedRowItems.map((virtualRow) => {
          const row = rows[virtualRow.index];

          return (
            <TableBodyRow
              key={row.id}
              row={row}
              virtualRow={virtualRow}
              rowVirtualizer={rowVirtualizer}
            />
          );
        })
      ) : (
        <tr>
          <td
            colSpan={columnsLength}
            className="h-24 p-4 text-center text-muted-foreground"
          >
            No results.
          </td>
        </tr>
      )}
    </tbody>
  );
}
